import './ExpenseForm.css';
import { useState } from 'react';

const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    const [showForm, setShowForm] = useState(false);

    const titleChangeHandler = event => {
        setEnteredTitle(event.target.value);
    };

    const amountChangeHandler = event => {
        setEnteredAmount(event.target.value);
    };

    const dateChangeHandler = event => {
        setEnteredDate(event.target.value);
    };

    const submitHandler = event => {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate + 'T00:00:00'),
    };
        props.onSaveExpenseData(expenseData);
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    };

    if (showForm) {
        return (
            <div className="expense-form">
                <form onSubmit={submitHandler}>
                    <label htmlFor="expense-title">Title</label>
                    <input type="text" id="expense-title" value={enteredTitle} onChange={titleChangeHandler} />
                    <label htmlFor="expense-amount">Amount</label>
                    <input type="number" id="expense-amount" value={enteredAmount} onChange={amountChangeHandler} />
                    <label htmlFor="expense-date">Date</label>
                    <input type="date" id="expense-date" value={enteredDate} onChange={dateChangeHandler} />
                    <button type="submit">Save Expense</button>
                </form>
            </div>
        );
    }

    return <form onSubmit={submitHandler}>
        <div className='new-expense__controls'>
            <div className='new-expense__control'>
                <label>Title</label> 
                <input type='text' value={enteredTitle} onChange={titleChangeHandler}/>
            </div> 
            <div className='new-expense__control'>
                <label>Amount</label> 
                <input type='number' min="0.01" step="0.01" value={enteredAmount} onChange={amountChangeHandler}/>
            </div> 
            <div className='new-expense__control'>
                <label>Date</label> 
                <input type='date' min="2019-01-01" max="2022-12-31" value={enteredDate} onChange={dateChangeHandler}/>
            </div> 
        </div>
        <div className='new-expense__actions'> 
            <CancelButton />
            <button type='submit'>Add Expense</button>
        </div>
    </form>
};

export default ExpenseForm;
